const NodeRSA = require('node-rsa');

export function generateRSAKeyPair() {
    const key = new NodeRSA();
    key.generateKeyPair(128);
    const publicKeyDer = key.exportKey("pkcs1-publick-der");
    const privateKeyDer = key.exportKey("pkcs8-private-der");

    const publicKey = new Buffer(publicKeyDer , 'binary').toString('base64');
    const privateKey = new Buffer(privateKeyDer , 'binary').toString('base64');

    const keyPair = {
        publicKey: publicKey,
        privateKey: privateKey
    }

    return keyPair;
}
