const { generateKeyPairSync } = require('crypto');

export function generateRSAKeyPair() {
    const { publicKey, privateKey } = generateKeyPairSync('rsa', {
        modulusLength: 4096,
        publicKeyEncoding: {
          type: 'spki',
          format: 'pem'
        },
        privateKeyEncoding: {
          type: 'pkcs8',
          format: 'pem',
          cipher: 'aes-128-cbc',
          passphrase: 'top secret'
        }
      });

    const keyPair = {
        publicKey: publicKey,
        privateKey: privateKey
    }
    return keyPair;
}
