const CryptoJS = require("crypto-js");

export function encryptAES_CBC(key, source) {

    let keyBytes = CryptoJS.enc.Base64.parse(key);

    console.log(keyBytes)

    let iv = CryptoJS.lib.WordArray.random(192 / 8);

    let encryptedSource = CryptoJS.AES.encrypt(source, keyBytes, {
        iv: iv,
        padding: CryptoJS.pad.Pkcs7,
        mode: CryptoJS.mode.CBC
    })

    console.log("IV: " + iv.toString());
    console.log("KEY: " + key.toString());

    return iv.toString() + encryptedSource.toString();
}
