const CryptoJS = require("crypto-js");

export function encryptAES_CBC(key, iv, source) {

    let keyBytes = CryptoJS.enc.Hex.parse(key);
    let ivBytes = CryptoJS.enc.Hex.parse(iv)

    console.log("Key: " + keyBytes)
    console.log("IV: " + ivBytes)

    let encryptedSource = CryptoJS.AES.encrypt(source, keyBytes, {
        iv: ivBytes,
        padding: CryptoJS.pad.Pkcs7,
        mode: CryptoJS.mode.CBC
    })

    return encryptedSource.toString();
}
