import React from 'react'
import Form from './Form';
import { generateRSAKeyPair } from './RSAGenerator'

export default class Main extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            publicKey: null,
            privateKey: null,
            token: null
        }
    }

    async onSubmit() {
        const publicKey = this.state.publicKey;
        if (publicKey) {

            const body = {
                key: publicKey
            };

            const response = await fetch("http://localhost:9000/auth/token/request", {
                method: "POST",
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(body)
            });

            const json = await response.json();

            console.log(json);

            this.setState({
                token: json.key
            });
        } else {
            alert('No public key generated');
        }

    }

    generateKeys() {
        const keyPair = generateRSAKeyPair();
        this.setState({
            publicKey: keyPair.publicKey,
            privateKey: keyPair.privateKey
        })
    }


    render() {
        return (
            <div>
                <p>{this.state.publicKey}</p>
                <p>{this.token}</p>
                <Form onSubmit={() => this.onSubmit()} />
                <button type="button" onClick={() => this.generateKeys()}>Generate Keys</button>
            </div >
        )
    }
}
