import React from 'react'
import Form from './Form';
import { generateRSAKeyPair, decryptTokenRSA } from './RSAUtils'

export default class Main extends React.Component {
    constructor(props) {
        super(props);

        const key = new NodeRSA().generateKeyPair(512)

        this.state = {
            publicKey: null,
            privateKey: null,
            token: null,
            rsa: key 
        }
    }

    async onSubmit() {
        const publicKey = this.state.publicKey;
        if (publicKey) {

            const body = {
                key: publicKey
            };

            const response = await fetch("http://localhost:9000/auth/token/request", {
                method: "POST",
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(body)
            });

            const json = await response.json();

            const token = json.token;

            const decryptedToken = decryptTokenRSA(token, this.state.privateKey);

            console.log(decryptedToken);

            this.setState({
                token: decryptedToken
            });
        } else {
            alert('No public key generated');
        }

    }

    generateKeys() {
        const keyPair = generateRSAKeyPair();
        this.setState({
            publicKey: keyPair.publicKey,
            privateKey: keyPair.privateKey
        })
    }
    


    render() {
        return (
            <div>
                <p>{this.state.publicKey}</p>
                <p>{this.token}</p>
                <Form onSubmit={() => this.onSubmit()} />
                <button type="button" onClick={() => this.generateKeys()}>Generate Keys</button>
            </div >
        )
    }
}
